mapper.select:
       SELECT * FROM (
        SELECT (@rownum := @rownum + 1) AS rownum,aa.* FROM
        (SELECT @rownum := 0) r,
        (

        select * from (

        SELECT  DISTINCT t1.`id` companyId ,t1.`name` companyName ,t1.`linkman`,t1.`linkman_phone`,t1.`address`,t1.`create_date` createDate,t1.`check_status` checkStatus,t1.status
        FROM sys_company t1, sys_dept t3
        WHERE
        t1.`company_type`=0  AND t1.`xgdw`=t3.`id`AND t1.`status`=1

            AND t1.`check_status`=#{params.checkStatus}

        AND ISNULL(t1.user_id) AND t1.xgdw =(SELECT dept_id FROM `sys_user` WHERE id=   #{params.userId})

        UNION

        SELECT  DISTINCT t1.`id` companyId ,t1.`name` companyName ,t1.`linkman`,t1.`linkman_phone`,t1.`address`,t1.`create_date` createDate ,t1.`check_status` checkStatus,t1.status
        FROM sys_company t1,
        (
	        SELECT * FROM sys_user t WHERE(
				0 =(SELECT is_dept_head FROM `sys_user` WHERE id= #{params.userId})
				AND
				t.dept_id IN (
				 SELECT id FROM sys_dept JOIN (SELECT getDeptChildList((SELECT dept_id FROM `sys_user` WHERE id=  #{params.userId})) AS idArr) TEMP WHERE FIND_IN_SET(id, TEMP.idArr)
		                 OR id = (SELECT dept_id FROM `sys_user` WHERE id= #{params.userId})

					 ) OR (
						t.id = #{params.userId}
						)
				                           )
        ) t2, sys_dept t3
  WHERE
    t1.`status`=1 AND t1.`company_type`=0  AND t2.`dept_id`=t3.`id` AND t1.`user_id`=t2.`id`

     AND t1.`check_status`=#{params.checkStatus}

        order by createDate  desc
        ) bb
        )aa
        ) c where
        c.rownum > ${params.lastRowNum}
        LIMIT ${params.pageSize}

        SELECT * FROM (
        SELECT (@rownum := @rownum + 1) AS rownum,aa.* FROM
        (SELECT @rownum := 0) r,
        (

         SELECT * FROM (

        SELECT  DISTINCT t1.`id` companyId ,t1.`name` companyName ,t1.`linkman`,t1.`linkman_phone`,t1.`address`,t1.`create_date` createDate,t1.`status`,t1.`check_status` checkStatus
        FROM sys_company t1, sys_dept t3
        WHERE
        t1.`company_type`=0  AND t1.`xgdw`=t3.`id` AND  t1.`status`IN (${params.status}) AND ISNULL(t1.user_id) AND t1.xgdw =(SELECT dept_id FROM `sys_user` WHERE id=   #{params.userId})

        UNION

       SELECT  DISTINCT t1.`id` companyId ,t1.`name` companyName ,t1.`linkman`,t1.`linkman_phone`,t1.`address`,t1.`create_date` createDate,t1.`status`,t1.`check_status` checkStatus
        FROM sys_company t1,
        (
	        SELECT * FROM sys_user t WHERE(
				0 =(SELECT is_dept_head FROM `sys_user` WHERE id=  #{params.userId})
				AND
				t.dept_id IN (
				 SELECT id FROM sys_dept JOIN (SELECT getDeptChildList((SELECT dept_id FROM `sys_user` WHERE id=  #{params.userId})) AS idArr) TEMP WHERE FIND_IN_SET(id, TEMP.idArr)
		                 OR id = (SELECT dept_id FROM `sys_user` WHERE id= #{params.userId})

					 ) OR (
						t.id = #{params.userId}
						)
				                           )
        ) t2, sys_dept t3
  WHERE
     t1.`company_type`=0  AND t2.`dept_id`=t3.`id` AND t1.`user_id`=t2.`id` AND  t1.`status`IN (${params.status})


        ) bb   ORDER BY bb.createDate DESC

        )aa
        ) c where
        c.rownum > ${params.lastRowNum}
        LIMIT ${params.pageSize}

       SELECT  COUNT(*)
        FROM sys_company t1,
        (
	        SELECT * FROM sys_user t WHERE(
				0 =(SELECT is_dept_head FROM `sys_user` WHERE id= #{userId})
				AND
				t.dept_id IN (
				 SELECT id FROM sys_dept JOIN (SELECT getDeptChildList((SELECT dept_id FROM `sys_user` WHERE id=  #{userId})) AS idArr) TEMP WHERE FIND_IN_SET(id, TEMP.idArr)
		                 OR id = (SELECT dept_id FROM `sys_user` WHERE id= #{userId})

					 ) OR (
						t.id = #{userId}
						)
				                           )
        ) t2, sys_dept t3
  WHERE
     t1.`company_type`=0  AND t2.`dept_id`=t3.`id` AND t1.`user_id`=t2.`id` AND  t1.`status`=0
